graphql.query:
mutation($name: String!, $gender: String!, $email: String!) {
    createUser( input: {
            clientMutationId: "0x123"
            name: $name
            gender: $gender
            email: $email
            status: "active"
        }
    )
    {
        user {
            id name gender email status
        }
    }
}

mutation($user_id: Int!, $title: String!, $body: String!) {
    createPost( input: {
            userId: $user_id
            title: $title
            body: $body
        }
    )
    {
        post {
            id userId title body
        }
    }
}

mutation($post_id: Int!, $name: String!, $email: String!) {
    createComment( input: {
            postId: $post_id
            name: $name
            email: $email
            body: "{{$randomLoremWords}}"
        }
    )
    {
        comment {
            id postId name email body
        }
    }
}

mutation($user_id: Int!, $title: String!, $due_on: ISO8601DateTime!, $status: String!) {
    createTodo( input: {
            userId: $user_id
            title: $title
            dueOn: $due_on
            status: $status
        }
    )
    {
        todo {
            id userId title dueOn status
        }
    }
}

query{
    users {
        pageInfo {
            endCursor startCursor hasNextPage hasPreviousPage
        } 
        edges { cursor }
        totalCount
    }
}

query($cursor: String!){
    users(last: 10, before: $cursor) {
        nodes {
            id name email gender status
        }
    }
}

query($cursor: String!){
    users(first: 10, after: $cursor) {
        nodes {
            id name email gender status
        }
    }
}

query($user_id: ID!){
    user(id: $user_id) {
        id name gender email status
    }
}

mutation($user_id: Int!) {
    deleteUser( input: {
            id: $user_id
        }
    )
    {
        user {
            id name gender email status
        }
    }
}
